import React from "react";
import {Contact} from "./MyProfile";
import {Field} from "redux-form";
import {Input, Textarea} from "../../common/FormsControls/FormsControl";
import classes from "../../Messages/Chatbar/Chatbar.module.css";
import {required} from "../../../utils/validation/validators";

const ProfileDataForm = (props) => {
    return <form onSubmit={props.handleSubmit}>
        <button class='btn btn-primary' onClick={props.offEditMode}>save</button>
        <div><b>About me</b>:<Field  className={classes.sms} component={Textarea} name={'newMessageBody'}
                                     placeholder={'Enter your message...'} validate={[required]}/></div>
        <div><b>lookingForAJob</b>:<input/></div>
        <div><b>lookingForAJobDescription</b>:<input/></div>
        <div><b>Contacts</b>:{Object.keys(props.profile.contacts).map(key => {
                return <div>
                    <Contact key={key} contactTitle={key} contactValue={props.profile.contacts[key]}/>
                    <input/>
                </div>
            }
        )}
        </div>
    </form>
}
export default ProfileDataForm;
