import React, {useState} from "react";
import classes from './Paginator.module.css';

const Paginator = (totalPageCount, pageSize, currentPage, onPageChanged, portionSize = 10) => {

    let pagesCount = Math.ceil(totalPageCount / pageSize)
    let pages = []
    for (let i = 1; i <= pagesCount; i++) {
        if (i <= 20) {
            pages.push(i)
        }
    }

    let portionCount = Math.ceil(pagesCount/portionSize)
    let [portionNumber, setPortionNumber] = useState(0)

    return <div>
        {pages.map(p => {
            return <span className={props.currentPage === p ? classes.selectedPage : classes.pageNumber}
                         onClick={(e) => {
                             props.onPageChanged(p)
                         }}>{p}</span>
        })}
    </div>
}

export default Paginator;
