import React, {useState} from "react";
import {Contact} from "./MyProfile";
import {Field, reduxForm} from "redux-form";
import {Input} from "../../common/FormsControls/FormsControl";
import {Textarea} from "../../common/FormsControls/FormsControl";



const ProfileDataForm = (props) => {
    let [fullName, setStatus] = useState(props.profile.fullName)

    const onStatusChange = (e) => {
        setStatus(e.target.value
        )
    }

    return <form onSubmit={props.handleSubmit}>
        <button class='btn btn-primary'>save</button>
        <div><b> Full name: <Field class='form-control' onChange={onStatusChange} component={Input} name={'fullName'} type='input'/></b></div>
        <div><b>looking for a job</b>: <Field component={Input} name={'lookingForAJob'} type='checkbox'/></div>
        <div><b>My professional skills</b>: <Field class='form-control' component={Textarea} name={'lookingForAJobDescription'} type='input'/></div>
        <div><b>About me</b>: <Field class='form-control' component={Input} name={'aboutMe'} type='input'/></div>
        {/*<div><b>Contacts</b>:{Object.keys(props.profile.contacts).map(key => {*/}
        {/*        return <div>*/}
        {/*            <Contact key={key} contactTitle={key} contactValue={props.profile.contacts[key]}/>*/}
        {/*            <input/>*/}
        {/*        </div>*/}
        {/*    }*/}
        {/*)}*/}
        {/*</div>*/}
    </form>
}

const ProfileDataReduxForm = reduxForm({form: 'edit-profile'})(ProfileDataForm)

export default ProfileDataReduxForm;
