import React from "react";
import styles from './FormsControl.module.css'
import TextField from "@material-ui/core/TextField";
import Checkbox from '@material-ui/core/Checkbox';
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormControl from "@material-ui/core/FormControl";
import InputLabel from "@material-ui/core/InputLabel";
import FormHelperText from "@material-ui/core/FormHelperText";

const FormCon = (props) => {
    const hasError = props.meta.touched && props.meta.error;
    return (
        <div className={styles.formControl + " " + (hasError ? styles.error : "")}>
            <div>
                {props.children}
            </div>
            {hasError && <span> {props.meta.error} </span>}
        </div>

    )
}

export const Textarea = (props) => {
    const {input, meta, child, ...restProps} = props
    return <div>
        <FormCon {...props}><TextField  {...input} {...restProps}
                                        multiline variant="outlined"/></FormCon>

    </div>
}
export const Input = (props) => {
    const {input, meta, child, ...restProps} = props
    return <FormCon {...props}><TextField {...input} {...restProps}
                                          variant="outlined"/></FormCon>
}

export const Check = (props) => {
    const {input, meta, child, ...restProps} = props
    return <FormCon {...props}><FormControlLabel control={<Checkbox {...input} {...restProps} color="primary"
                                                                    inputProps={{'aria-label': 'secondary checkbox'}}
    />} label='Remember me'/></FormCon>
}
export const Checked = (props) => {
    const {input, meta, child, ...restProps} = props
    return <FormCon {...props}><FormControlLabel control={<Checkbox {...input} {...restProps} color="primary"
                                                                    inputProps={{'aria-label': 'secondary checkbox'}}
    />}/></FormCon>
}


export const Log = (props) => {
    const {input, meta, child, ...restProps} = props
    return <FormCon {...props}><TextField {...input} {...restProps} label="Email" id="standard-basic"
    /></FormCon>
}

export const Pass = (props) => {
    const {input, meta, child, ...restProps} = props
    return <FormCon {...props}><TextField {...input} {...restProps} id="standard-password-input"
                                          label="Password" type="password"
                                          autoComplete="current-password"
    /></FormCon>
}


export const Logon = (props) => {
    // const {input, meta, child, ...restProps} = props
    const hasError = props.meta.touched && props.meta.error;
    if (hasError) {
        return <FormCon {...props}>
            <FormControl {...props} error>
                <InputLabel htmlFor="component-error">Name</InputLabel>
                <Input {...props}
                       id="component-error"
                       aria-describedby="component-error-text"
                />
                <FormHelperText id="component-error-text">Error</FormHelperText>
            </FormControl>
        </FormCon>
    } else {
        return  <FormCon {...props}><TextField {...props} label="Email" id="standard-basic"
        /></FormCon>
    }
}
