import React, {useState} from "react";
import styles from './Paginator.module.css';

const Paginator = (totalPageCount, pageSize, currentPage, onPageChanged, portionSize = 10) => {

    let pagesCount = Math.ceil(totalPageCount / pageSize)
    let pages = []
    for (let i = 1; i <= pagesCount; i++) {
        if (i <= 20) {
            pages.push(i)
        }
    }

    let portionCount = Math.ceil(pagesCount/portionSize)
    let [portionNumber, setPortionNumber] = useState(1)
    let leftPortionNumber = (portionNumber - 1) * portionSize + 1
    let rightPortionNumber = portionNumber * portionSize

    return <div className={styles.paginator}>
        {pages
            .map(p => {
            return <span className={currentPage === p ? styles.selectedPage : styles.pageNumber}
                         onClick={(e) => {
                             onPageChanged(p)
                         }}>{p}</span>
        })}
    </div>
}

export default Paginator;
