import React from 'react';
import styles from './Chatbar.module.css';
import Chat from "./Chat/Chat";
import {Field, reduxForm} from "redux-form";
import {Textarea} from "../../common/FormsControls/FormsControl";
import {maxLengthCreator, required} from "../../../utils/validation/validators";
import {makeStyles} from "@material-ui/core/styles";
import Button from '@material-ui/core/Button';
import Icon from "@material-ui/core/Icon";


const Chatbar = (props) => {
    let messageElements = props.messagesPage.messages.map(m => <Chat message={m.message} avatar={m.ava} id={m.id}/>)

    let addNewMessage = (values) => {
        props.addMessage(values.newMessageBody)
        values.newMessageBody = ''
    }

    return (
        <div className={styles.chatbar}>
            {messageElements}
            <div>
                <AddMessageReduxForm onSubmit={addNewMessage}/>
            </div>
        </div>
    )
}

const AddMessageForm = (props) => {
    const useStyles = makeStyles((theme) => ({
        root: {
            '& > *': {
                margin: theme.spacing(1),
                width: '150px',
                float: 'right',
                marginRight: '13px',
                marginBottom: '10px'
            }
        },
        field: {
            '& .MuiTextField-root': {
                margin: theme.spacing(1),
                width: '78ch'
            }
        }
    }));
    const classes = useStyles();

    return (
        <form onSubmit={props.handleSubmit}>
            <div className={classes.field}>
                <Field component={Textarea} name={'newMessageBody'}
                       placeholder={'Enter your message...'}/>
            </div>
            <div className={classes.root}>
                <Button onClick={props.handleSubmit}
                        variant="contained"
                        color="primary"
                        className={classes.button}
                        endIcon={<Icon>send</Icon>}
                >
                    Send
                </Button>
                {/*<button >*/}
                {/*    /!*<Button variant="outlined" color="primary">send</Button>*!/*/}
                {/*    send*/}
                {/*</button>*/}
            </div>
        </form>
    )
}

const AddMessageReduxForm = reduxForm({form: 'dialogAddMessageForm'})(AddMessageForm)

export default Chatbar;
