import React from 'react';
import styles from './MyProfile.module.css';
import Preloader from "../../common/Preloader/Preloader";
import ProfileStatusWithHooks from "./ProfileStatusWithHooks";
import userPhoto from '../../../assets/images/user.png';

const MyProfile = (props) => {
  if (!props.profile.photos) {
    return <Preloader/>
  }

  const onSelectedPhoto = (e) => {
    if (e.target.files.length) {
      props.savePhoto(e.target.files[0])
    }
  }

  return (
    <div className={styles.content}>
      <div>
        <span className={styles.avatar}>
          <img src={!props.profile.photos.large ?userPhoto : props.profile.photos.large}/>
          {props.isOwner && <div className="input__wrapper">
            <input name="file" type="file" name="file" onChange={onSelectedPhoto}
                   id="input__file" className="input input__file" multiple/>
              <label htmlFor="input__file" className="input__file-button">
              <span className="input__file-icon-wrapper"><img className="input__file-icon" src="./img/add.svg"
                                                              alt="Выбрать файл" width="25"/>
              </span>
                <span className="input__file-button-text">Выберите файл</span>
              </label>
          </div>}
        </span>



        <div className={styles.profile}>
          <p className={styles.name}>{props.profile.fullName}</p>
          < ProfileStatusWithHooks {...props} status={props.status} updateStatus={props.updateStatus}/>
          <p>About Me: {props.profile.aboutMe}</p>
          <p >Facebook: <a href='https://ru-ru.facebook.com/'>{props.profile.contacts.facebook}</a></p>
        </div>
      </div>
    </div>
  )
}

export default MyProfile;
