import React, {useState} from 'react';
import styles from './MyProfile.module.css';
import Preloader from "../../common/Preloader/Preloader";
import ProfileStatusWithHooks from "./ProfileStatusWithHooks";
import userPhoto from '../../../assets/images/user.png';
import ProfileDataForm from "./ProfileDataForm";
import Button from "@material-ui/core/Button";


const MyProfile = (props) => {

    const [editMode, setEditMode] = useState(false)

    if (!props.profile.photos) {
        return <Preloader/>
    }

    const onSelectedPhoto = (e) => {
        if (e.target.files.length) {
            props.savePhoto(e.target.files[0])
        }
    }
    const onSubmit = (formData) => {
        props.saveProfile(formData).then( () => {
            setEditMode(false)
        })
    }

    return (
        <div className={styles.content}>
            <div class='row'>
                <div class='col-5' className={styles.avatar}>
                    <img src={!props.profile.photos.large ? userPhoto : props.profile.photos.large}/>
                    {props.isOwner &&
                    <div className={classes.root}>
                        <input
                            accept="image/*"
                            className={classes.input}
                            id="contained-button-file"
                            multiple
                            type="file"
                        />
                        <label htmlFor="contained-button-file">
                            <Button variant="contained" color="primary" component="span">
                                Upload
                            </Button>
                        </label>
                    </div>}
                </div>
                    {/*// <div className={styles.button}>*/}
                    {/*//     <label class="btn btn-default">*/}
                    {/*//         Change photo <input name='file' type="file" onChange={onSelectedPhoto} hidden/>*/}
                    {/*//     </label>*/}
                     {/*</div>*/}
                    {/*}*/}
                {/*</div>*/}
                <div class='col-7' className={styles.profile}>
                    <div><b>{props.profile.fullName}</b></div>
                    <ProfileStatusWithHooks {...props} status={props.status} updateStatus={props.updateStatus}/>
                    {editMode
                        ? <ProfileDataForm initialValues={props.profile} onSubmit={onSubmit} {...props}/>
                        : <ProfileData onEditMode={() => {
                            setEditMode(true)
                        }} {...props}/>}
                </div>
            </div>
        </div>
    )
}

const ProfileData = (props) => {
    return <div>
        {props.isOwner && <div>
            <button class='btn btn-default' onClick={props.onEditMode}>edit profile</button>
        </div>}
        {/*<div><b>Full name: {props.profile.fullName}</b></div>*/}
        <div><b>Looking for a job</b>: {props.profile.lookingForAJob ? 'Yes' : 'No'} </div>
        <div><b>My professional skills</b>: {props.profile.lookingForAJobDescription}</div>
        <div><b>About me</b>: {props.profile.aboutMe}</div>
        {/*<div><b>Contacts</b>: {Object.keys(props.profile.contacts).map(key => {*/}
        {/*        return <Contact key={key} contactTitle={key} contactValue={props.profile.contacts[key]}/>*/}
        {/*    }*/}
        {/*)}*/}
        {/*</div>*/}


        <div className='dropdown'>
            <button className='btn dropdown-toggle btn-default' data-toggle='dropdown'><b>Contacts</b></button>
            <ul className='dropdown-menu'>
                <li>
                    {Object.keys(props.profile.contacts).map(key => {
                            return <Contact key={key} contactTitle={key} contactValue={props.profile.contacts[key]}/>
                        }
                    )}
                </li>
            </ul>
        </div>


    </div>
}

export const Contact = ({contactTitle, contactValue}) => {
    return <div className={styles.contact}><b>{contactTitle}</b>: <a href={contactValue}>{contactValue}</a></div>
}

export default MyProfile;
