import React from "react";
import {Contact} from "./MyProfile";
import {Field, reduxForm} from "redux-form";
import {Input} from "../../common/FormsControls/FormsControl";
import styles from "../../Login/Login.module.css";

const ProfileDataForm = (props) => {
    return <form>
        <button class='btn btn-primary' onClick={props.offEditMode}>save</button>
        <div><b>About me</b>:<Field component={Input} name={'aboutMe'}/></div>
        <div><b>lookingForAJob</b>:<input/></div>
        <div><b>lookingForAJobDescription</b>:<input/></div>
        <div><b>Contacts</b>:{Object.keys(props.profile.contacts).map(key => {
                return <div>
                    <Contact key={key} contactTitle={key} contactValue={props.profile.contacts[key]}/>
                    <input/>
                </div>
            }
        )}
        </div>
    </form>
}

const ProfileDataReduxForm = reduxForm({form: 'aboutMe'})(ProfileDataForm)

export default ProfileDataReduxForm;
