import React from "react";
import styles from './FormsControl.module.css'
import TextField from "@material-ui/core/TextField";
import Checkbox from '@material-ui/core/Checkbox';
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormControl from "@material-ui/core/FormControl";
import InputLabel from "@material-ui/core/InputLabel";
import InputAdornment from "@material-ui/core/InputAdornment";
import IconButton from "@material-ui/core/IconButton";
import clsx from "clsx";

const FormsControl = ({input, meta, child, ...props}) => {
    const hasError = meta.touched && meta.error;
    return (
        <div className={styles.formControl + " " + (hasError ? styles.error : "")}>
            <div>
                {props.children}
            </div>
            {hasError && <span> {meta.error} </span>}
        </div>
    )
}

export const Textarea = (props) => {
    const {input, meta, child, ...restProps} = props
    return <div>
        <FormsControl {...props}><TextField  {...input} {...restProps}
                                             multiline variant="outlined"/></FormsControl>

    </div>
}
export const Input = (props) => {
    const {input, meta, child, ...restProps} = props
    return <FormsControl {...props}><TextField {...input} {...restProps}
                                               variant="outlined"/></FormsControl>
}

export const Check = (props) => {
    const {input, meta, child, ...restProps} = props
    return <FormsControl {...props}><FormControlLabel control={<Checkbox {...input} {...restProps} color="primary"
                                                                         inputProps={{'aria-label': 'secondary checkbox'}}
    />} label='Remember me'/></FormsControl>
}
export const Checked = (props) => {
    const {input, meta, child, ...restProps} = props
    return <FormsControl {...props}><FormControlLabel control={<Checkbox {...input} {...restProps} color="primary"
                                                                         inputProps={{'aria-label': 'secondary checkbox'}}
    />}/></FormsControl>
}


export const Password = (props) => {
    const useStyles = makeStyles((theme) => ({
        root: {
            display: 'flex',
            flexWrap: 'wrap',
        },
        margin: {
            margin: theme.spacing(1),
        },
        withoutLabel: {
            marginTop: theme.spacing(3),
        },
        textField: {
            width: '25ch',
        },
    }));

    const classes = useStyles();


    const {input, meta, child, ...restProps} = props
    return <FormsControl {...props}><FormControl
        className={clsx(classes.margin, classes.textField)} {...input} {...restProps}>
        <InputLabel htmlFor="standard-adornment-password">Password</InputLabel>
        <Input
            id="standard-adornment-password"
            type={values.showPassword ? 'text' : 'password'}
            value={values.password}
            onChange={handleChange('password')}
            endAdornment={
                <InputAdornment position="end">
                    <IconButton
                        aria-label="toggle password visibility"
                        onClick={handleClickShowPassword}
                        onMouseDown={handleMouseDownPassword}
                    >
                        {values.showPassword ? <Visibility/> : <VisibilityOff/>}
                    </IconButton>
                </InputAdornment>
            }
        />
    </FormControl>
    </FormsControl>

    // <FormControl {...props}><TextField {...input} {...restProps}
    //                                           variant="outlined"/></FormControl>
}
